cloudnav/colors.scss:
// basically repeat above
$dark_brand: #30261d;
$dark: #333333;
$darker: darken($dark, 4%);
// $dark: #282b39;
$Ldark: lighten($dark, 3%);
$light: #F5F5F5;
$green: #39C178;
$yellow: #FF9D00;
$red: #c72e1f;
$white: #FFFFFF;
$black: #000000;
$blue: #1489DD;
$dblue: #202135;
$purple: $green;
$orange: #ff6b00;
$dgray: #333;
$facebook: #3B5998;
$google: #DB4317;


$colors:
	"dark" $dark,
	"darker" $darker,
	"Ldark" $Ldark,
	"light" $light,
	"green" $green,
	"yellow" $yellow,
	"red" $red,
	"white" $white,
	"black" $black,
	"blue" $blue,
	"dblue" $dblue,
	"purple" $purple,
	"orange" $orange,
	"facebook" $facebook,
	"google" $google,
	"dgray" $dgray
;


@each $color in $colors{
	// .btn-color
	.btn-#{nth($color, 1)}{
	    background: nth($color, 2);
	    color: #fff;
	}
	// .color / color
	.#{nth($color, 1)}, #{nth($color, 1)}{
	    color: nth($color, 2) !important;
	    a{
	        color: nth($color, 2);
	    }
	}
	// .color-bg
	.#{nth($color, 1)}-bg {
		background: nth($color, 2) !important;
	}

	// .color-g
	.#{nth($color, 1)}-g {
		background: rgb(30,87,153);
		background: -moz-linear-gradient(top, nth($color, 2) 0%, adjust-hue(nth($color, 2),15%) 100%);
		background: -webkit-linear-gradient(top, nth($color, 2) 0%, adjust-hue(nth($color, 2),15%) 100%);
		background: linear-gradient(to bottom, nth($color, 2) 0%, adjust-hue(nth($color, 2),15%) 100%);
		filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='$color', endColorstr='#7db9e8',GradientType=0 );
	}
}













@mixin grad($deg, $c1, $c2, $fixed) {
	// background-image: -webkit-linear-gradient($deg, $c1, $c2) !important;
	// background-image: linear-gradient($deg, $c1, $c2) !important;
	// background-color: $c2;
	background-image: linear-gradient($deg, $c1, $c2);
	background-image: -webkit-linear-gradient($deg, $c1, $c2);   
	background-image:    -moz-linear-gradient($deg, $c1, $c2);
	background-image:     -ms-linear-gradient($deg, $c1, $c2);
	background-image:      -o-linear-gradient($deg, $c1, $c2);
	background-repeat: no-repeat;
	background-size: cover;
	background-attachment: $fixed;

}

cloudnav/slick.scss:
/* Slider */
.slick-slider
{
    position: relative;

    display: block;
    box-sizing: border-box;

    -webkit-user-select: none;
       -moz-user-select: none;
        -ms-user-select: none;
            user-select: none;

    -webkit-touch-callout: none;
    -khtml-user-select: none;
    -ms-touch-action: pan-y;
        touch-action: pan-y;
    -webkit-tap-highlight-color: transparent;
}

.slick-list
{
    position: relative;

    display: block;
    overflow: hidden;

    margin: 0;
    padding: 0;
}
.slick-list:focus
{
    outline: none;
}
.slick-list.dragging
{
    cursor: pointer;
    cursor: hand;
}

.slick-slider .slick-track,
.slick-slider .slick-list
{
    -webkit-transform: translate3d(0, 0, 0);
       -moz-transform: translate3d(0, 0, 0);
        -ms-transform: translate3d(0, 0, 0);
         -o-transform: translate3d(0, 0, 0);
            transform: translate3d(0, 0, 0);
}

.slick-track
{
    position: relative;
    top: 0;
    left: 0;

    display: block;
}
.slick-track:before,
.slick-track:after
{
    display: table;

    content: '';
}
.slick-track:after
{
    clear: both;
}
.slick-loading .slick-track
{
    visibility: hidden;
}

.slick-slide
{
    display: none;
    float: left;

    height: 100%;
    min-height: 1px;
}
[dir='rtl'] .slick-slide
{
    float: right;
}
.slick-slide img
{
    display: block;
}
.slick-slide.slick-loading img
{
    display: none;
}
.slick-slide.dragging img
{
    pointer-events: none;
}
.slick-initialized .slick-slide
{
    display: block;
}
.slick-loading .slick-slide
{
    visibility: hidden;
}
.slick-vertical .slick-slide
{
    display: block;

    height: auto;

    border: 1px solid transparent;
}
.slick-arrow.slick-hidden {
    display: none;
}










/*
   /$$     /$$
  | $$    | $$
 /$$$$$$  | $$$$$$$   /$$$$$$  /$$$$$$/$$$$   /$$$$$$
|_  $$_/  | $$__  $$ /$$__  $$| $$_  $$_  $$ /$$__  $$
  | $$    | $$  \ $$| $$$$$$$$| $$ \ $$ \ $$| $$$$$$$$
  | $$ /$$| $$  | $$| $$_____/| $$ | $$ | $$| $$_____/
  |  $$$$/| $$  | $$|  $$$$$$$| $$ | $$ | $$|  $$$$$$$
   \___/  |__/  |__/ \_______/|__/ |__/ |__/ \_______/

*/


/* Slider */
.slick-loading .slick-list
{
    background: #fff url('./ajax-loader.gif') center center no-repeat;
}

/* Icons */
@font-face
{
    font-family: 'slick';
    font-weight: normal;
    font-style: normal;

    src: url('../fonts/slick.eot');
    src: url('../fonts/slick.eot?#iefix') format('embedded-opentype'), url('../fonts/slick.woff') format('woff'), url('../fonts/slick.ttf') format('truetype'), url('../fonts/slick.svg#slick') format('svg');
}
/* Arrows */
.slick-prev,
.slick-next
{
    font-size: 0;
    line-height: 0;

    position: absolute;
    top: 50%;

    display: block;

    width: 20px;
    height: 20px;
    padding: 0;
    -webkit-transform: translate(0, -50%);
    -ms-transform: translate(0, -50%);
    transform: translate(0, -50%);

    cursor: pointer;

    color: transparent;
    border: none;
    outline: none;
    background: transparent;
}
.slick-prev:hover,
.slick-prev:focus,
.slick-next:hover,
.slick-next:focus
{
    color: transparent;
    outline: none;
    background: transparent;
}
.slick-prev:hover:before,
.slick-prev:focus:before,
.slick-next:hover:before,
.slick-next:focus:before
{
    opacity: 1;
}
.slick-prev.slick-disabled:before,
.slick-next.slick-disabled:before
{
    opacity: .25;
}

.slick-prev:before,
.slick-next:before
{
    font-family: 'slick';
    font-size: 20px;
    line-height: 1;

    opacity: .75;
    color: white;

    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.slick-prev
{
    left: -25px;
}
[dir='rtl'] .slick-prev
{
    right: -25px;
    left: auto;
}
.slick-prev:before
{
    content: '←';
}
[dir='rtl'] .slick-prev:before
{
    content: '→';
}

.slick-next
{
    right: -25px;
}
[dir='rtl'] .slick-next
{
    right: auto;
    left: -25px;
}
.slick-next:before
{
    content: '→';
}
[dir='rtl'] .slick-next:before
{
    content: '←';
}

/* Dots */
.slick-dotted.slick-slider
{
    margin-bottom: 30px;
}

.slick-dots
{
    position: absolute;
    bottom: -25px;

    display: block;

    width: 100%;
    padding: 0;
    margin: 0;

    list-style: none;

    text-align: center;
}
.slick-dots li
{
    position: relative;

    display: inline-block;

    width: 20px;
    height: 20px;
    margin: 0 5px;
    padding: 0;

    cursor: pointer;
}
.slick-dots li button
{
    font-size: 0;
    line-height: 0;

    display: block;

    width: 20px;
    height: 20px;
    padding: 5px;

    cursor: pointer;

    color: transparent;
    border: 0;
    outline: none;
    background: transparent;
}
.slick-dots li button:hover,
.slick-dots li button:focus
{
    outline: none;
}
.slick-dots li button:hover:before,
.slick-dots li button:focus:before
{
    opacity: 1;
}
.slick-dots li button:before
{
    font-family: 'slick';
    font-size: 6px;
    line-height: 20px;

    position: absolute;
    top: 0;
    left: 0;

    width: 20px;
    height: 20px;

    content: '•';
    text-align: center;

    opacity: .25;
    color: black;

    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
.slick-dots li.slick-active button:before
{
    opacity: .75;
    color: black;
}





.slick-navi{
    text-align: center;
    margin-top: 2em;
    li{
        a{
            color: #efefef;
            padding: 1em;
            font-size: 1.2em;
            &:hover{ color: $green; }
        }
    }
}

cloudnav/responsive.scss:
$media-queries: true;





@mixin bp($point) {
  @if ($media-queries) {





	// desktop
     $bp-XL-desktop: 2560px; //mac 27"
     $bp-L-desktop: 1924px;  //home monitor 27
     $bp-M-desktop: 1500px;  //mac 15"
     $bp-S-desktop: 1300px;  //mac 13"

    @if $point == XL-desktop {
      @media (max-width: #{$bp-XL-desktop}){
        @content;
      }
    }

    @if $point == L-desktop {
      @media (max-width: #{$bp-L-desktop}){
        @content;
      }
    }

    @if $point == M-desktop {
      @media (max-width: #{$bp-M-desktop}){
        @content;
      }
    }

    @if $point == S-desktop {
      @media (max-width: #{$bp-S-desktop}){
        @content;
      }
    }




    // tablet
    $bp-XL-tablet: 1124px;
    $bp-L-tablet: 1024px;
    // $bp-M-tablet: 768px;
    $bp-M-tablet: 800px;

    @if $point == XL-tablet {
      @media (max-width: #{$bp-XL-tablet}){
        @content;
      }
    }

    @if $point == L-tablet {
      @media (max-width: #{$bp-L-tablet}){
        @content;
      }
    }

    @if $point == M-tablet {
      @media (max-width: #{$bp-M-tablet}){
        @content;
      }
    }






    // phone
    $bp-L-phone: 678px;
    $bp-M-phone: 480px;
    $bp-S-phone: 320px;

    @if $point == L-phone {
      @media (max-width: #{$bp-L-phone}){
        @content;
      }
    }

    @if $point == M-phone {
      @media (max-width: #{$bp-M-phone}){
        @content;
      }
    }

    @if $point == S-phone {
      @media (max-width: #{$bp-S-phone}){
        @content;
      }
    }


    @if $point == L-phone-o {
      @media (min-width: #{$bp-L-phone}){
        @content;
      }
    }

    @if $point == M-phone-o {
      @media (min-width: #{$bp-M-phone}){
        @content;
      }
    }

    @if $point == S-phone-o {
      @media (min-width: #{$bp-S-phone}){
        @content;
      }
    }

    @if $point == phone-port {
      @media only screen
        and (min-device-width: 580px) 
        and (max-device-width: 990px) 
        and (orientation: landscape) 
      {
        @content;
      }
    }




  }
}






.hide-on-mobile{
  display: block;
}

.mobile-only{
  display: none;
}

@include bp(L-phone){
  .hide-on-mobile{
    display: none;
  }
  .mobile-only{
    display: block;
  }
}




// zoom
@include bp(L-phone){
  select,
  textarea,
  input {
    font-size: 16px;
  }

  select:focus,
  textarea:focus,
  input:focus {
