.slick-slide { -webkit-transform: translate3d(0,0,0); }
.heroCarousel {
	height: 100vh;
	img {
		max-height: inherit;
	}
	.slick-dots {
	    bottom: 1rem;
		li {
			background: transparent;
			width: initial;
			height: initial;
			button {
				border: 0;
				width: initial;
				height: initial;
				&:before { 
					background: rgba(#ffffff,0.2);
					height: 0.1em;
					width: 0.6em;
    				border-radius: 0.1em;
    				position: relative;
    				&:hover {
    					opacity: 0.2;
    				}
				}
			}
			&.slick-active button::before { background: $purple; }
		}
	}
	.slick-next, .slick-prev {
		background: transparent;
		border: 0;
		opacity: 0.2;
		&:hover {
			opacity: 1;
		}
		@include bp(L-tablet) {
		    right: 80px !important;
		}

	}
	@include bp(S-desktop) {
		height: inherit;
		min-height: 30em;
	}
	@include bp(L-tablet) {
		margin: 0;
		height: 100vh;
		img {
			object-fit: cover; // wont work on Firefox
			height: 100vh;
    		width: 100%;
    		object-fit: cover;
		}
		.slick-list, .slick-track, .heroCarousel-slide {
			display: block;
		    width: 100%;
		    height: 100%;
		    img {
		    	width: 100%;
		    	max-height: 100%;
		    	height: 100%;
		    }
		}
	}
	@include bp(L-phone) {
		height: 59vh;
	}
}
