ul.contextMenu{
	list-style:none; 
	margin:0;padding:0;
	font: 300 15px 'Roboto', sans-serif;
	position: absolute;
	color: #333;
	box-shadow: 0 12px 15px 0 rgba(0, 0, 0, 0.2);
	width: 16em;
	z-index: 9;
	form{
		background: $light;
		padding: 1em;
		margin-top: 5px;
		input {
		border: 1px solid rgba(#ccc, 0.5);
			display: block;
			padding: 0.2em;
			margin-bottom: 5px;
			width: 100%;
			&[type="submit"] {
				width: 100%;
			}
		}
	}
	.info {
		background: $dark;
		padding: 0.6em;
		color: #fff;
		margin-bottom: 1em;
		@extend .clearfix;
		p.z_id {
			float: left;
			span.zone_id {
				font-weight: bold;
				padding-bottom: 0.4em;
			}
		}
		p.dims {
			color: $yellow;
			float: right;
			font-weight: bold;
		}
	}
}

ul.contextMenu *{
    transition:color .4s, background .4s;
}

ul.contextMenu li{
  min-width:150px;
  overflow: hidden;
  white-space: nowrap;
  padding: 12px 15px;
  background-color: #fff;
  border-bottom:1px solid #ecf0f1;
}

ul.contextMenu li a{
  color:#333;
  text-decoration:none;
}

ul.contextMenu li:hover{
  background-color: #ecf0f1;
}

ul.contextMenu li:first-child{
  border-radius: 5px 5px 0 0;
}

ul.contextMenu li:last-child{
  background:#ecf0f1;
  border-bottom:0;
  border-radius: 0 0 5px 5px
}

ul.contextMenu li:last-child a{width:26%;}
ul.contextMenu li:last-child:hover a{color:#2c3e50}
ul.contextMenu li:last-child:hover a:hover{color:#2980b9}

