$right-color: #fff;
$right-bg: lighten($dark, 3%);
$right-accent: $purple;

.Product {
	.icon {
		height: 1.2rem;
    	width: 1.2rem;
	}
	.productPage {
		&>.columns {
			@include bp(M-tablet) {
				background: $right-bg;
			}
		}
		.col-left {
			position: relative;
			z-index: 1;
			@extend .p0;
			@extend .m0;
		}
		.col-right {
			// margin-bottom: 20em; // should be the same as cta bar
			// min-height: 100vh;
			margin-bottom: 10em;
			@extend .p0;
			// @extend .m0;
			@include bp(M-tablet) {
				min-height: inherit;
				margin-bottom: 2em;
			}
		}
		.product-left {
			position: relative;
			top: 3em;
			.productView-images {
				float: none;
				width: 100% !important;
				// text-align: center;
				text-align: right;
				&.dz-drag-hover{
					background: rgba($blue,0.2);
					border: 1px solid $blue;
				}
				.admin-add a {
					background: $light;
					i{
						@include centerer;
					}
				}
				.slick-list {
				    // min-width: 75px;
				}
				span{ width: 100% !important;}
				.features {
					margin-bottom: -4em;
					padding: 3em 0;
					display: block;
					text-transform: uppercase;
					letter-spacing: 1px;
					font-size: 0.9em;
					font-weight: bold;
					color: $dark;
					position: relative;
					z-index: 5;
					text-align: right;
					display: inline-block;
					&:hover {
						color: $blue;
					}
					@include bp(M-tablet) {
						color: $yellow;
						padding-right: 3em;
					}
				}
				@include bp(L-phone) {
					background: #fff;
				}
			}
			.productView-img-container:after {display: none;}
			.productView-img-container {
				max-width: 100% !important;
				// cursor: zoom-in;
				cursor: auto;
				position: relative;
				a {
					display: block;
					padding: 2em 1em;
					text-align: center;
					// cursor: zoom-in;
					cursor: auto;
					@include bp(L-phone) {
						padding: 0;
					    margin-top: 35px;
					}
					img {
						// cursor: zoom-in;
						cursor: auto;
					    position: static;
			    	    // width: 100%;
    					margin: 0 auto;
					    // width: 318px; // same as image below
					}
				}
			}
			.overlay-feat {
				position: absolute;
			    top: 5em;
			    left: 0;
			    right: 0;
			    z-index: 9;
			    display: none;
			    text-align: center;
				img {
					width: 100%;
					height: 100%;
				    width: 618px; // same as image above
				}
			}
			ul.productView-thumbnails {
				// display: inline-block;
				position: absolute;
    			z-index: 10;
    			top: 2em;
    			left: 2em;
    			max-width: 10em; // some were too wide
				li.productView-thumbnail {
					position: relative;
				    width: 100%;
					span {
						position: absolute;
						top: -1em;
						right: -1em;
						height: 2em;
						width: 2em;
						border-radius: 100%;
						background: $dark;
						z-index: 1;
						color: #fff;
						font-size: 0.7em;
						i{
							font-size: 0.8em;
							@include centerer;
						}
						&:hover{
							background: $red;
						}
					}
					a.productView-thumbnail-link {
						// border: 0;
						margin-bottom: 0.25em;
						background: #fff;
						img {
							position: static;
						}
					}
				}
				.only-images {
					// position: relative;
				}
				@include bp(L-phone) {
					position: static;
				    left: 1em;
				    max-width: 100%;
				    margin: 3em 0;
				    li {
				    	width: initial !important;
				    }
				}
			}
			@include bp(L-phone) {
				top: 0;
			}
			label.form-label.form-label--alternate {
			    color: #fff;
			}
		}
		.dz-image-preview {opacity: 0 !important;}
		.qty-controls label {
			color: #fff !important;
		}
		.product-right {
			color: $right-color;
			background: $right-bg;
			padding: 3em;
			padding-top: 7em;
			// position: relative;
			// height: 100%;
			.form-field.form-field--stock {
				display: none;
			}
			@include bp(M-tablet) {
				padding-top: 3em;
				// margin-top: -5em;

			}
			.form-label {
				color: #fff;
			}
			.product-alerts {
			    position: absolute;
			    top: 0;
			    left: 0;
			    width: 100%;
			    padding: 1em;
			    padding: 2em;
			    .alertBox {
			    	background: rgba($right-color,0.06) !important;
			    	color: $right-color !important;
			    	position: relative;
			    	&:after {
			    		font-family: Fontawesome;
						content: "\f057";
						right: 1em;
						top: 30%;
						position: absolute;
						opacity: 0.3;
						color: $green;
			    	}
					&:hover:after {
						opacity: 1;
					}
			    }
			}
			.right-head {
				h1 {
					color: $right-color;
				}
				h2 {
					a{
						color: $right-accent;
					}
				}
			}
			.product-tabs {
				margin: 2em 0;
				.tab1s {
					position: sticky;
					top: -1px;
					background: $right-bg;
					z-index: 1;
					ul {
						border-bottom: 1px solid rgba($right-color, 0.25);
						li {
							&:first-child a{
								padding-left: 0;
							}
							a {
								color: $right-color;
								opacity: 0.5;
								padding-bottom: 0.75em;
								border-bottom: 2px transparent;
								margin-bottom: -1px;
								font-weight: 600;
								letter-spacing: 0.04em;
								&:hover {
									opacity: 1;
								}
							}
							&.is-active {
								a {
									// border-bottom: 2px solid $right-accent;
									opacity: 1;
								}
							}
						}
					}
					.underline {
		                border-bottom: 3px solid $right-accent;
		                bottom: 0;
		                height: 100%;
		                left: 0px;
		                position: absolute;
		                width: 0px;
		                transition: all 0.3s cubic-bezier(0.68, -0.30, 0.265, 1.30);
		                z-index: 2;
		            }
				}
				.tab-content-container {
					display: block;
					.content {
						display: none;
						&.is-active {
							display: block;
						}
					}
				}
				.description {
					padding-right: 5em;
					max-height: 105px;
					overflow: hidden;
					&.open {
						max-height: 100% !important;
					}
					@include bp(L-phone) {
						padding: 0;
					}
				}
				.showButton {
					display: block;
					@extend .mono;
					padding: 0;
					color: $yellow;
					padding-top: 1em;
				}
			}
			h3 {
				color: $right-color;
				border-bottom: 1px solid rgba($right-color, 0.1);
				text-transform: capitalize;
				padding-bottom: 0.5em;
			}
			.productView2-details {
				.form-label--alternate {
					opacity: 0;
					height: 0.5em; // use as padding
				}
				select {
					border: 0;
					background: rgba($right-color, 0.1);
				}
			}
		}
		.form-radio + .form-option {
			border: 0 !important;
			background: $dark !important;
			color: #fff;
			span {
				padding: 0.25em;
				// width: 7em;
				text-align: center;
				border: 0 !important;
				padding: 0.25em 1em !important;
			}
			&:hover {}
		}
		.form-radio:checked + .form-option {
			background: $green !important;
		}
		.cta-bar {
			background: darken($right-bg, 4%);
			padding: 1em 3em;
			padding-bottom: 0.1em;
			position: sticky;
			bottom: -0.2em;
			z-index: 10;
			.price-container {
				span.price {
					@extend .mono;
					color: $yellow;
					font-weight: bold;
					font-size: 1.1em;
				}
			}
			.productView-info-value a {
				color: rgba(#fff, 0.4);
				font-size: 0.9em;
				text-transform: capitalize;
				&:hover {
					color: #fff;
				}
			}
			.qty-and-add {
				.qty-controls {
					button {
						border-radius: 100%;
						width: 2.1em;
						height: 2.1em;
						background: $right-bg;
						border: 0;
						i {
							@extend .centerer;
						}
						&:hover i svg{
							fill: $right-color;
						}
					}
					label {
						@extend .p;
						font-weight: 600;
						color: lighten($right-bg, 3%);
					}
					input {
						color: $right-color;
						background: transparent;
					}
				}
				.add-button {

				}
			}
			@include bp(L-phone) {
				// .level-left {display: none;}
			}
		}
		.product-below {
			ul.tabs {
				border: 0;
				border-bottom: 1px solid rgba(#000,0.08);
				li {
					&.is-active {
						border: 0;
						border-bottom: 2px solid $purple;
					}
				}
				@include bp(M-tablet) {
					@extend .inline;
					li {
						border: 0;
						margin-right: 1em;
					}
				}
			}
		}
	}
	.addthis_toolbox {text-align: left;}
	.socialLinks {
		display: inline-block;
		margin: 0;
	}
	#wishlist-dropdown {
		@extend .inline;
		margin: 0;
		.button {
			background: #fff;
			border: 0;
			margin-right: 10px;
		}
	}


}

