.custom-designs {
	background: $dark;
	.design-header {
		@extend .clearfix;
		background: url(https://airows.com/.image/c_limit%2Ccs_srgb%2Cq_auto:good%2Cw_1112/MTI4OTk3MzgyODg4MjM3Njk5/mark-weldon-briefs-shirts-07.webp);
	    background-size: cover;
	    margin-bottom: 5em; // counter the .explain box
	    p.f {
	    	@extend .mono;
	    	font-weight: bold;
	    	color: $purple;
	    	font-size: 1em;
	    	font-size: 1.2em;
    font-style: italic;
    text-align: center;
    opacity: 0.6;
    margin-bottom: 2em;
	    }
		.tall-title {
			font-size: 2em;
			text-align: left;
			@extend .main_font;
			padding-bottom: 0.5em;
		}
		.explain {
			background: $white;
			max-width: 27em;
			float: left;
			margin-bottom: -5em;
			margin-top: 5em;
			margin-left: 3em;
			padding: 2em;
			p {
				text-align: justify;
			}
		}
	}
	.guts {
		h3 {
			@extend .curl;
			text-transform: capitalize;
			font-size: 2em;
			font-weight: 500;
			letter-spacing: 0;
			text-align: center;
		}
		.sec {
			padding: 5em 0;
		}
		.sec.steps {
			$step-top: $light;
			$step-btm: $dark;
			background: $step-btm;
			color: $step-top;
			padding: 4em;
			padding-bottom: 7em;
			@include bp(L-phone) {
				padding: 2em 0;
			}
			h3 {
				text-align: center;
				color: inherit;
			}
			p.f {
				text-align: center;
			}
			.step-item {
				position: relative;
				padding: 1.5em;
				.num {
					position: absolute;
					left: 0;
					top: 0.3em;
					height: 3em;
					width: 3em;
					color: $step-top;
					font-size: 1.2em;
					background: darken($step-btm,3%);
					border: 0.35em solid $step-btm;
					border-radius: 100%;
					font-weight: bolder;
					z-index: 1;
					span {
						@include centerer;
						@extend .curl;
					}
				}
				.img-content {
					position: relative;
					background: #000;
					img {
					}
					h6 {
						// @include centerer;
					}
					img {
						// opacity: 0.8;
						height: 10em;
						object-fit: cover;
						width: 100%;
					}
					margin-bottom: 1em;
				}
				p {
					text-align: justify;
				}
				h6 {
					font-weight: bold;
					letter-spacing: 1px;
					margin: 0.5em 0;
					color: $yellow;
				}
			}
		}
		.sec.pricing {
			background: #fff;
			text-align: center;
			.current.price {
				text-align: center;
				font-size: 3em;
				margin: 0.5em 0;
				color: $green;
				span.per {
					font-size: 0.5em;
					font-weight: 400;
					color: #ccc;
					text-transform: capitalize;
					@extend .opens;
					letter-spacing: 0;
				}
				@include bp(L-phone) {
					display: none; // table instead
				}
			}
			p.tac {
				font-size: 1.2em;
			    font-style: italic;
			    text-align: center;
			    opacity: 0.6;
			    // margin-bottom: 2em;
			}
			.pricing-table {
				display: none;
				@include bp(L-phone) {
					display: block;
				}
			}
			.pricing-list {
				display: inline-block;
				text-align: center;
				padding-top: 2em;
				border-bottom: 1px solid #ccc;
				width: 50%;
				position: relative;
				@include bp(L-phone) {
					width: 105%;
					margin-left: -0.75em;
					display: none; // table instead
				}
				&:after, &:before {
					content: " ";
					position: absolute;
					right: 0;
					top: 0;
					bottom: 0;
					padding: 1em;
					z-index: 1;
					@include grad(90deg, transparent, $white, cover);
					bottom: -19px;
				}
				&:before {
					left: 0;
					right: inherit;
					@include grad(270deg, transparent, $white, cover);
				}
				li {
					width: 11%;
					a {
						color: #ccc;
						// height: 4em;
						// width: 4em;
						position: relative;
						// background: $light;
						// border-radius: 100%;
						font-weight: bold;
					    padding-top: 3em;
						position: relative;
						@include ease(0.2s);
						&:after {
							content: '|';
							color: #ccc;
							opacity: 1;
							@include ease(0.2s);
						}
						.qty {
							height: 1em;
							@include centerer;
						}
						.price {display: none;}
						&:hover {
							color: $purple;
						}
						&.cur {
							color: $blue;
							.qty {
								font-size: 1.2em;
							}
							&:after {
								opacity: 0;
							}
						}
					}
				}
				.line {
					font-weight: bolder;
					color: $purple;
					font-size: 1.2em;
					// margin-bottom: -0.2em;
					position: absolute;
					bottom: -1px;
					@include ease(0.2s);
					margin-left: 0px;
				}
			}
		}
	}
}


