cloudnav/faq.scss:
#faq {


%extend_1 {
  float: none;
  display: inline-block;
  *display: inline;
  zoom: 1;
}

%extend_2 {
  content: "";
  display: block;
  height: 1em;
  width: 100%;
  display: none;
}

%extend_3 {
  background-color: $light;
  font-size: 1em;
  line-height: 1.5em;
}

%extend_4 {
  background-color: #fff;
  text-align: center;
  font-weight: 500;
  padding: 2em;
  display: block;
  text-decoration: none;
  color: $dark;
  transition: background-color 0.2s ease-in-out;
  border-bottom: 1px solid $light;
}

%extend_5 {
  content: " +";
  font-size: 1.5em;
  line-height: 0.5em;
  float: left;
  transition: transform 0.3s ease-in-out;
}

ul.contextMenu {
  p.form-g {
    padding-top: 0.5em;
