    font-weight: bold;
    &:hover {
      text-decoration: underline;
    }
  }
}

body {
  background: #fff;
}
.container {
  &.p2em.main-contain {
    width: 100%;
    max-width: 100%;
  }
}
.tab-pane {
  display: none;
  &.active {
    display: block;
  }
}
.p2em {
  padding: 0 !important;
}
.nav-tabs {
  text-align: center;
  > li {
    @extend %extend_1;
  }
}
.nav-pills {
  text-align: center;
  > li {
    @extend %extend_1;
  }
}
.info-header {
  background: $dark;
  padding-top: 2em;
  padding: 4em;
  @include bp(L-phone) {
    padding: 1em;
    .sub-title {
      padding: 0;
      margin-top: 1em;
    }
  }
  p {
    text-align: center;
    line-height: 26px;
    color: $light;
    @extend p.f;
  }
  h1 {
    color: $yellow;
    font-size: 2.2em;
  }
}
dl {
  margin: 0 !important;
  dt {
    margin-bottom: 0;
  }
}
.main-contain {
  background: transparent;
  padding: 0;
}
.heading-primary {
  font-size: 2em;
  padding: 2em;
  text-align: center;
}
.accordion {
  position: relative;
  background-color: $light;
  dl {
    border: 1px solid $light;
    &:after {
      @extend %extend_2;
    }
  }
  dd {
    @extend %extend_3;
  }
  p {
    padding: 1em 2em;
  }
}
.accordion-list {
  border: 1px solid $light;
  &:after {
    @extend %extend_2;
  }
}
.accordion__panel {
  @extend %extend_3;
}
.accordionTitle {
  @extend %extend_4;
  &:before {
    @extend %extend_5;
  }
  &:hover {
    // background-color: $dark;
  }
  &.is-expanded {
    background-color: $dark;
    color: #fff;
    &:before {
      transform: rotate(-225deg);
    }
  }
}
.accordion__Heading {
  @extend %extend_4;
  &:before {
    @extend %extend_5;
  }
  &:hover {
    // background-color: $dark;
  }
}
.accordionTitleActive {
  background-color: $dark;
  color: #fff;
  &:before {
    transform: rotate(-225deg);
  }
}
.accordionItem {
  height: auto;
  overflow: hidden;
  max-height: 50em;
  transition: max-height 1s;
  &.is-collapsed {
    max-height: 0;
  }
  &.is-expanded {
    display: block;
    margin: 0 !important;
  }
}
@media screen and(min-width: 48em) {
  .accordionItem {
    transition: max-height 0.2s;
  }
}
.animateIn {
  animation: accordionIn 0.3s normal ease-in-out both 1;
}
.animateOut {
  animation: accordionOut 0.3s alternate ease-in-out both 1;
}
@-webkit-keyframes accordionIn {
  0% {
    opacity: 0;
    transform: scale(0.9) rotateX(-60deg);
    transform-origin: 50% 0;
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}
@keyframes accordionIn {
  0% {
    opacity: 0;
    transform: scale(0.9) rotateX(-60deg);
    transform-origin: 50% 0;
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}
@-webkit-keyframes accordionOut {
  0% {
    opacity: 1;
    transform: scale(1);
  }
  100% {
    opacity: 0;
    transform: scale(0.9) rotateX(-60deg);
  }
}
@keyframes accordionOut {
  0% {
    opacity: 1;
    transform: scale(1);
  }
  100% {
    opacity: 0;
    transform: scale(0.9) rotateX(-60deg);
  }
}
#info_tabs .nav-tabs {
