cloudnav/mixins.scss:
@mixin ease($speed) {
-webkit-transition: all $speed ease-in-out;
   -moz-transition: all $speed ease-in-out;
     -o-transition: all $speed ease-in-out;
    -ms-transition: all $speed ease-in-out;
        transition: all $speed ease-in-out;
}



@mixin delay($delay) {
          animation-delay:$delay;
  -webkit-animation-delay:$delay;
     -moz-animation-delay:$delay;
       -o-animation-delay:$delay;
}


@mixin ani($delay, $name, $dur) {
    -webkit-animation-duration: $dur;
    animation-duration: $dur;
    -webkit-animation-fill-mode: both;
    animation-fill-mode: both;
    -webkit-animation-name: $name;
    animation-name: $name;
          animation-delay:$delay;
  -webkit-animation-delay:$delay;
     -moz-animation-delay:$delay;
       -o-animation-delay:$delay;
}


@mixin dur($adur) {
        animation-duration:$adur;
-webkit-animation-duration:$adur;
   -moz-animation-duration:$adur;
     -o-animation-duration:$adur;
}

@mixin blur($blur) {
           filter: blur($blur);
      -moz-filter: blur($blur);
   -webkit-filter: blur($blur);
}
@mixin fixed_cover() {
  background-attachment: fixed;
  background-size: cover;
}

@mixin rotate($deg) {
    -ms-transform: rotate($deg);
    -webkit-transform: rotate($deg);
    transform: rotate($deg);
}



@mixin newFont($fontName) {
  @font-face {
      src: url('../fonts/$fontName.eot');
      src: url('../fonts/$fontName.eot') format('embedded-opentype'),
           url('../fonts/$fontName.woff2') format('woff2'),
           url('../fonts/$fontName.woff') format('woff'),
           url('../fonts/$fontName.ttf') format('truetype'),
           url('../fonts/$fontName.svg#$fontName') format('svg');
  }
}


.imgreflect{
  -webkit-box-reflect: below 2px -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(85%, transparent), to(rgba(0, 0, 0, 0.35)));
}

//-- Coverer --//
@mixin coverer {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

//-- Centerer --//
@mixin centerer {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

.centerer{
  @include centerer;
}


/* Mixin */
@mixin vertical-align($position: relative) {
  position: $position;
  top: 50%;
  -webkit-transform: translateY(-50%);
  -ms-transform: translateY(-50%);
  transform: translateY(-50%);
}
/*use*/
// .element p {
//   @include vertical-align();
// }

cloudnav/tooltip.scss:
[data-tooltip] {
  position: relative;
  z-index: 8;
}
[data-tooltip]::before,
[data-tooltip]::after {
  position: absolute;
  display: none;
  opacity: 0.85;
}
[data-tooltip]::before {
  /*
   * using data-tooltip instead of title so we 
   * don't have the real tooltip overlapping
   */
  content: attr(data-tooltip);
  background: #000;
  color: #fff;
  font-size: 13px;
  padding: 5px;
  border-radius: 5px;
  /* we don't want the text to wrap */
  white-space: nowrap;
  text-decoration: none;
}
[data-tooltip]::after {
  width: 0;
  height: 0;
  border: 6px solid transparent;
  content: '';
}

[data-tooltip]:hover::before,
[data-tooltip]:hover::after {
  display: block;
}

/** positioning **/

/* left tooltip */
[data-tooltip][data-placement="left"]::before {
  top: -25%;
  right: 100%;
  margin-right: 10px;
}
[data-tooltip][data-placement="left"]::after {
  border-left-color: #000;
  border-right: none;
  top: 50%;
  right: 100%;
  margin-top: -6px;
  margin-right: 4px;
}

/* right tooltip */
[data-tooltip][data-placement="right"]::before {
  top: -25%;
  left: 100%;
  margin-left: 10px;
}
[data-tooltip][data-placement="right"]::after {
  border-right-color: #000;
  border-left: none;
  top: 50%;
  left: 100%;
  margin-top: -6px;
  margin-left: 4px;
}

/* top tooltip */
[data-tooltip][data-placement="top"]::before {
  bottom: 100%;
  left: 0;
  margin-bottom: 10px;
}
[data-tooltip][data-placement="top"]::after {
  border-top-color: #000;
  border-bottom: none;
  bottom: 100%;
  left: 10px;
  margin-bottom: 4px;
}

/* bottom tooltip */
[data-tooltip][data-placement="bottom"]::before {
  top: 100%;
  left: 0;
  margin-top: 10px;
}
[data-tooltip][data-placement="bottom"]::after {
  border-bottom-color: #000;
  border-top: none;
  top: 100%;
  left: 10px;
  margin-top: 4px;
}

