section.home1 {
	.lft,.rght{position: relative;}
	.rght{
		.r1 {
			padding-bottom: 1.50rem;
		}
	}
	.bb{
		background: #000;
		display: block;
		@extend .pointer;
		&:hover img {
			opacity: 0.9;
		}
	}
	.pp {
		@extend .bb;
		position: absolute;
		top: 0;
	}
	.button + .button {
		margin-left: 0;
	}
	a.button {
		color: #fff;
		border: 0;
		height: inherit;
		font-weight: bold;
		&.save {
			margin-top: 1em;
			background: $green;
		}
		&.cancel {
			background: $red;
		}
		&:hover {
			background: darken($dark, 4%);
		}&:active {
			background: $purple;
		}
	}
	.confirm-upload {
	    position: absolute;
	    top: 0;
	    padding: 1em;
	    background: #000;
	    color: #fff;
	}
	.dz-remove {display: none;}
	.pr{
	    @include ease(0.1s);
	    &.dz-drag-hover {
    	    border: 3px solid $blue;
	    }
	}
	.slick-dotted.slick-slider {
		margin-bottom: 0;
	}
	.slick-list {
		// overflow: visible;
	}
}
.home-features {
	position: relative;
	// overflow: hidden;
	// height: 430px;
	z-index: 0;

	.bg {
		position: absolute;
		width: 100%;
		z-index: 1;
		top: 0;
		left: 0;
		bottom: 0;

		&.under {
			background: #000;
		}

		&.newletter-bg {
			background: url(http://worldchefstour.co.za/wp-content/uploads/2016/07/boerewors-south-african-sausage-2.jpg) fixed !important;
			background-size: cover !important;
			opacity: 0.5;
		}

		&.about-bg {
			background: url(https://cdn7.bigcommerce.com/s-kg0otnmpzw/product_images/uploaded_images/biltong-styled14.jpg?t=1538750492) fixed !important;
			background-size: cover !important;
			// opacity: 0.5;
		}
	}

	img.heading {
		margin: 1em 0;
	}

	h2.barlow {
		font-size: 3.5em !important;
		color: $yellow;
		letter-spacing: 0;
		font-weight: 500;
		font-style: italic;
		text-transform: capitalize;
	}

	p.barlow {
		// font-style: italic;
		font-size: 1.5em;
	}

	.over {
		@include centerer;
		z-index: 2;
	}

	.over-c {
		// position: absolute;
		color: #fff;
		padding: 6% 0;

		.container {
			position: relative;
			z-index: 1;
		}

		.grad-l {
			position: absolute;
			// z-index: 1;
			left: 0;
			right: 0;
			top: 0;
			bottom: -1px;
			z-index: 1;
			background: -moz-linear-gradient(left, rgba(0, 0, 0, 1) 20%, rgba(0, 0, 0, 0.16) 100%);
			background: -webkit-linear-gradient(left, rgba(0, 0, 0, 1) 20%, rgba(0, 0, 0, 0.16) 100%);
			background: linear-gradient(to right, rgba(0, 0, 0, 1) 20%, rgba(0, 0, 0, 0.16) 100%);
			filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#000000', endColorstr='#29000000', GradientType=1);
		}

		.columns {
			position: relative;
			z-index: 1;

			.left-col {}

			.right-col {}
		}
	}

	h5 {
		text-align: center;
	}

	.field {
		p {
			input {
				background: rgba(#000, 0.5);
				color: #fff;
				border: 0.1em solid $yellow;

				&:focus {
					border: 0.1em solid $purple;
				}
			}
		}

		.fa-check {
			opacity: 0.2;
			color: $green;
			@include ease(0.2s);
		}

		&.good {
			input:focus {
				border-color: $green;
			}

			.fa-check {
				opacity: 1;
			}
		}
	}

	::-webkit-input-placeholder {
		/* Chrome/Opera/Safari */
		color: rgba(#fff, 0.4);
		font-family: 'Libre Baskerville', Didot, "Didot LT STD", "Hoefler Text", Garamond, "Times New Roman", serif;
		font-style: italic;
	}

	::-moz-placeholder {
		/* Firefox 19+ */
		color: rgba(#fff, 0.4);
		font-family: 'Libre Baskerville', Didot, "Didot LT STD", "Hoefler Text", Garamond, "Times New Roman", serif;
		font-style: italic;
	}

	:-ms-input-placeholder {
		/* IE 10+ */
		color: rgba(#fff, 0.4);
		font-family: 'Libre Baskerville', Didot, "Didot LT STD", "Hoefler Text", Garamond, "Times New Roman", serif;
		font-style: italic;
	}

	:-moz-placeholder {
		/* Firefox 18- */
		color: rgba(#fff, 0.4);
		font-family: 'Libre Baskerville', Didot, "Didot LT STD", "Hoefler Text", Garamond, "Times New Roman", serif;
		font-style: italic;
	}

	.centerer-x {
		position: absolute;
		top: 50%;
		left: 36%;
		transform: translate(-50%, -50%);
		/* left: 0; */
		width: 50%;
		//    @include bp(L-phone) {
		// 	width: 100%;
		// 	position: static;
		// 	transform: translate(0%, 0%);
		// 	padding: 3em 1em;
		// }
	}

	@include bp(M-tablet) {
		// height: 50vh !important;

		.bg {
			bottom: 0;

			img {
				object-fit: cover;
				height: 100%;
				width: auto;
			}
		}

		.over {
			width: 70%;
			margin: 0 auto;
			text-align: center;
		}

		.centerer-x {
			width: 100%;
			position: static;
			transform: translate(0%, 0%);
			padding: 3em 1em;
		}
	}

	@include bp(phone-port) {
		// height: 278px;

		h5 {
			font-size: 1em;
		}

		.over {
			top: 40%;
		}
	}

	@include bp(L-phone) {
		// height: 55vh !important;
	}
}
