@if $exportCSS--accordion { // 1

    // Component
    // -------------------------------------------------------------------------

    .accordion {
        @include accordion-container;
        border: $accordion-border;
        border-width: $accordion-borderWidth;
        box-shadow: $accordion-boxShadow;
        margin: $accordion-margin;
    }

    .accordion-navigation {
        @include accordion-navigation($active_class: "is-open");
        border: $accordion-navigation-border;
        border-width: $accordion-navigation-borderWidth;
        cursor: $cursor-pointer-value;

        .accordion-title {
            padding: $accordion-title-padding;
        }

    }

    .accordion-title {
        font-weight: $accordion-title-fontWeight;
        line-height: $accordion-title-lineHeight;
    }

    .accordion-content {
        @include accordion-content($active_class: "is-open");
        border: $accordion-content-border;
        border-width: $accordion-content-borderWidth;
    }

    .accordion-navigation--success {
        .accordion-title,
        &.is-open > a {
            color: $accordion-title-successColor;   // 2
        }
    }

    .accordion-navigation--error {
        .accordion-title,
        &.is-open > a {
            color: $accordion-title-errorColor;   // 2
        }
    }

    .accordion-navigation--warning {
        .accordion-title,
        &.is-open > a {
            color: $accordion-title-warningColor;   // 2
        }
    }

}

