    font-size: 60px;
    line-height: 80px;
    text-align: center;
  }

  &.swal2-success {
    border-color: $swal2-success;

    [class^='swal2-success-circular-line'] { // Emulate moving circular line
      border-radius: 50%;
      position: absolute;
      width: 60px;
      height: 120px;
      transform: rotate(45deg);

      &[class$='left'] {
        border-radius: 120px 0 0 120px;
        top: -7px;
        left: -33px;

        transform: rotate(-45deg);
        transform-origin: 60px 60px;
      }

      &[class$='right'] {
        border-radius: 0 120px 120px 0;
        top: -11px;
        left: 30px;

        transform: rotate(-45deg);
        transform-origin: 0 60px;
      }
    }

    .swal2-success-ring { // Ring
      width: 80px;
      height: 80px;
      border: 4px solid $swal2-success-border;
      border-radius: 50%;
      box-sizing: content-box;

      position: absolute;
      left: -4px;
      top: -4px;
      z-index: 2;
    }

    .swal2-success-fix { // Hide corners left from animation
      width: 7px;
      height: 90px;

      position: absolute;
      left: 28px;
      top: 8px;
      z-index: 1;

      transform: rotate(-45deg);
    }

    [class^='swal2-success-line'] {
      height: 5px;
      background-color: $swal2-success;
      display: block;
      border-radius: 2px;

      position: absolute;
      z-index: 2;

      &[class$='tip'] {
        width: 25px;

        left: 14px;
        top: 46px;

        transform: rotate(45deg);
      }

      &[class$='long'] {
        width: 47px;

        right: 8px;
        top: 38px;

        transform: rotate(-45deg);
      }
    }
  }
}

.swal2-progresssteps {
  $lightblue: #add8e6;
  $blue: #3085d6;

  font-weight: 600;
  margin: 0 0 20px;
  padding: 0;

  li {
    display: inline-block;
    position: relative;
  }

  .swal2-progresscircle {
    background: $blue;
    border-radius: 2em;
    color: $swal2-white;
    height: 2em;
    line-height: 2em;
    text-align: center;
    width: 2em;
    z-index: 20;

    &:first-child {
      margin-left: 0;
    }

    &:last-child {
      margin-right: 0;
    }

    &.swal2-activeprogressstep {
      background: $blue;

      ~ .swal2-progresscircle {
        background: $lightblue;
      }

      ~ .swal2-progressline {
        background: $lightblue;
      }
    }
  }

  .swal2-progressline {
    background: $blue;
    height: .4em;
    margin: 0 -1px;
    z-index: 10;
  }
}


// github.com/limonte/sweetalert2/issues/268
[class^='swal2'] {
  -webkit-tap-highlight-color: $swal2-transparent;
}


// Modal animations

@keyframes showSweetAlert {
  0% {
    transform: scale(.7);
  }

  45% {
    transform: scale(1.05);
  }

  80% {
    transform: scale(.95);
  }

  100% {
    transform: scale(1);
  }
}

@keyframes hideSweetAlert {
  0% {
    transform: scale(1);
    opacity: 1;
  }

  100% {
    transform: scale(.5);
    opacity: 0;
  }
}

.swal2-show {
  animation: showSweetAlert .3s;

  &.swal2-noanimation {
    animation: none;
  }
}

.swal2-hide {
  animation: hideSweetAlert .15s forwards;

  &.swal2-noanimation {
    animation: none;
  }
}



// Success icon animation

@keyframes animate-success-tip {
  0% {
    width: 0;
    left: 1px;
    top: 19px;
  }

  54% {
    width: 0;
    left: 1px;
    top: 19px;
  }

  70% {
    width: 50px;
    left: -8px;
    top: 37px;
  }

  84% {
    width: 17px;
    left: 21px;
    top: 48px;
  }

  100% {
    width: 25px;
    left: 14px;
    top: 45px;
  }
}

@keyframes animate-success-long {
  0% {
    width: 0;
    right: 46px;
    top: 54px;
  }

  65% {
    width: 0;
    right: 46px;
    top: 54px;
  }

  84% {
    width: 55px;
    right: 0;
    top: 35px;
  }

  100% {
    width: 47px;
    right: 8px;
    top: 38px;
  }
}

@keyframes rotatePlaceholder {
  0% {
    transform: rotate(-45deg);
  }

  5% {
    transform: rotate(-45deg);
  }

  12% {
    transform: rotate(-405deg);
  }

  100% {
    transform: rotate(-405deg);
  }
}

.swal2-animate-success-line-tip {
  animation: animate-success-tip .75s;
}

.swal2-animate-success-line-long {
  animation: animate-success-long .75s;
}

.swal2-success {
  &.swal2-animate-success-icon {
    .swal2-success-circular-line-right {
      animation: rotatePlaceholder 4.25s ease-in;
    }
  }
}


// Error icon animation

@keyframes animate-error-icon {
  0% {
    transform: rotateX(100deg);
    opacity: 0;
  }

  100% {
    transform: rotateX(0deg);
    opacity: 1;
  }
}

.swal2-animate-error-icon {
  animation: animate-error-icon .5s;
}

@keyframes animate-x-mark {
  0% {
    transform: scale(.4);
    margin-top: 26px;
    opacity: 0;
  }

  50% {
    transform: scale(.4);
    margin-top: 26px;
    opacity: 0;
  }

  80% {
    transform: scale(1.15);
    margin-top: -6px;
  }

  100% {
    transform: scale(1);
    margin-top: 0;
    opacity: 1;
  }
}

.swal2-animate-x-mark {
  animation: animate-x-mark .5s;
}

@keyframes rotate-loading {
  0% {
    transform: rotate(0deg);
  }

  100% {
    transform: rotate(360deg);
  }
}

components/vendor/sweetalert2/sweetalert:
// SWEETALERT2
// -----------------------------------------------------------------------------
//
// Purpose: Styles for the vendor popup box replacement, SweetAlert2
//
// -----------------------------------------------------------------------------
//
// Purpose: Override the plugin to give some Stencil styling
//
// -----------------------------------------------------------------------------

.swal2-modal {
    background-color: $alert-modal-bgColor;
    border-radius: $alert-modal-borderRadius;
    font-family: $alert-font-body;

    .swal2-title {
        color: $alert-font-headingsColor;
        font-size: $alert-font-headingsSize;
    }

    .swal2-content {
        color: $alert-font-bodyColor;
        font-size: $alert-font-bodySize;
    }

    .swal2-file,
    .swal2-input,
    .swal2-textarea {
        border: 1px solid $alert-input-borderColor;
        color: $alert-input-color;
    }

    .swal2-checkbox,
    .swal2-select {
        color: $alert-input-color;
    }

    .swal2-buttonswrapper {
        margin: $alert-button-wrapperMargin;
    }

    .swal2-styled {
        @include button-base;
        @include buttonVariant("default");
        @include buttonSize("default");
        border-radius: $button-radius;
        line-height: 1rem;
        outline: none;
        transition: all 0.15s ease;
        vertical-align: $button-vAlign;
    }

    .swal2-styled:focus {
        box-shadow: $button-focus-boxShadow;
        outline: none;
    }

    .swal2-confirm {
        background-color: $alert-button-bgColor;
        border-color: $alert-button-borderColor;
        color: $alert-button-color;
    }

    .swal2-confirm:focus,
    .swal2-confirm:hover {
        background-color: $alert-button-bgColorHover;
        border-color: $alert-button-borderColorHover;
        color: $alert-button-colorHover;
    }

    .swal2-confirm:active {
        background-color: $alert-button-bgColorActive;
        border-color: $alert-button-borderColorActive;
        color: $alert-button-colorActive;
    }

    .swal2-cancel {
        background-color: $alert-button-cancel-bgColor;
        border-color: $alert-button-cancel-borderColor;
        color: $alert-button-cancel-color;

        @include breakpoint("xsmall") {
            margin-left: $button-adjacentButton-marginLeft;
        }
    }

    .swal2-cancel:focus,
    .swal2-cancel:hover {
        background-color: $alert-button-cancel-bgColorHover;
        border-color: $alert-button-cancel-borderColorHover;
        color: $alert-button-cancel-colorHover;
    }

    .swal2-cancel:active {
        background-color: $alert-button-cancel-bgColorActive;
        border-color: $alert-button-cancel-borderColorActive;
        color: $alert-button-cancel-colorActive;
    }
}

