components/foundation/tables/tables.scss:
// =============================================================================
// TABLES (CSS)
// =============================================================================


// Stencil table component additions
// -----------------------------------------------------------------------------

.table {
    margin-top: $table-marginTop;

    td + td {
        border-left: $table-td-borderLeft;
    }

    .table-alignCenter {
        text-align: center;
    }

    .table-alignRight {
        text-align: right;
    }
}

.table-actions {
    margin: spacing("single");
    text-align: center;

    .form {
        margin-bottom: 0;
    }

    .button {
        display: block;
        margin-left: 0;
        margin-right: 0;
        width: 100%;

        @include breakpoint("small") {
            display: inline-block;
            margin-bottom: 0;
            width: auto;
        }

        // scss-lint:disable NestingDepth
        + .button {

            @include breakpoint("small") {
                margin-left: spacing("quarter");
            }
        }
        // scss-lint:enable NestingDepth
    }
}


// Stencil table modifiers
// -----------------------------------------------------------------------------

.table--line {
    border-color: container("borderColor");
    border-width: 0 0 1px;

    .table-thead {
        background-color: stencilColor("body-bg");
        border-color: container("borderColor");
        border-width: 0 0 1px;

        // scss-lint:disable SelectorDepth, NestingDepth
        tr {

            th {
                padding-left: 0;
                padding-right: 0;

                @include breakpoint("small") {
                    text-align: center;

                    &:first-child {
                        text-align: left;
                    }

                    &:last-child {
                        text-align: right;
                    }
                }

                + th {
                    padding-left: spacing("single");
                }
            }
        }
    }

    tr {
        border-color: container("border");

        // scss-lint:disable SelectorDepth, NestingDepth
        td {
            display: block;
            padding-left: 0;
            padding-right: 0;

            @include breakpoint("small") {
                text-align: center;

                &:first-child {
                    text-align: left;
                }

                &:last-child {
                    text-align: right;
                }
            }

            + td {
                padding-left: spacing("single");
            }
        }
    }

    td + td {
        border-left: 0;
    }
}

// =============================================================================
// DROPDOWN (CSS)
// =============================================================================


.dropdown {
    left: -9999px;
    outline: none;
    position: absolute;
}

.dropdown-menu {
    &.is-loading .loadingOverlay {
        background: none;
        padding: (spacing("double") + spacing("single")) 0;
        position: relative;
    }

    &.is-open {
        display: block;
    }
}

.dropdown-menu-item {
    text-align: left;

    &:first-child {

        // scss-lint:disable NestingDepth
        > a {
            padding-top: spacing("half");
        }
    }

    &:last-child {

        // scss-lint:disable NestingDepth
        > a {
            padding-bottom: spacing("base");
        }
    }

    > a {
        margin: 0;
        padding: spacing("sixth") (spacing("half") + spacing("quarter"));
        text-decoration: none;
    }
}

.form-wishlist {
    position: relative;
}

.dropdown-menu-button {
    width: 100%;

    &.is-open {
        border-bottom-left-radius: 0px;
        border-bottom-right-radius: 0px;
    }

    .icon {
        float: right;
        margin-left: 10px;
    }
}

#wishlist-dropdown.is-open {
    background: white;
    left: auto !important;
    max-width: none !important;
    width: 100% !important;

    @include breakpoint('small') {
        padding-right: 20px;
    }

    @include breakpoint('medium') {
        padding-right: 0px;
    }

    @include breakpoint('large') {
        padding-right: 11px;
    }

    &:focus {
        outline: none;
    }

    [data-wishlist] {
        border-top: none;
        margin-bottom: 0px;
        width: 100%;
    }

    .button {
        border-top-left-radius: 0px;
        border-top-right-radius: 0px;
        width: 100%;
    }

    .button--has-items {
        font-weight: 700;
    }

    li:not(:last-child) .button {
        border-bottom: none;
        border-radius: 0px;
        border-top: none;
        margin-bottom: 0px;
    }
}

components/foundation/modal/modal.scss:
