    + .banner {
        margin-top: spacing("single");
    }
}

components/stencil/facetLabel/facetLabel:
// =============================================================================
// FACET LABEL (CSS)
// =============================================================================

.facetLabel {
    background: $facetLabel-backgroundColor;
    border-radius: $facetLabel-borderRadius;
    padding: spacing("eighth") spacing("third") spacing("eighth") (spacing("third") + spacing("eighth"));
    text-decoration: none;

    .icon {
        @include square(12px);
        display: inline-block;
        fill: $facetLabel-removeIcon-color;
        position: relative; // Vertical align the icon
