<script>
    // check if msg was seen already
    $('[data-cookie]').each( function(){
        var cookie_name = $(this).data('cookie');
        if(!$.cookie(cookie_name)) {
            $(this).addClass('show');
        }else {
            console.log(cookie_name,' Already Seen.');
        }
    });

    // close msg
    $('.close').click(function() {
        var parent = $(this).closest('.closing');
        parent.hide();
        if(parent.data('cookie')){
            console.log('set-cookie: ', parent.data('cookie'));
            $.cookie(parent.data('cookie'), true, { expires: 1 });
        }
    });

    // reveal "more"
    $('.notifications .inner').click( function() {
        console.log($(this).find('.more'));
        $(this).find('.more').toggleClass('show');
    });
</script>

