components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

components/cloudnav/account-footer-links:
{{!-- account links --}}
{{#if settings.account_creation_enabled}}
  {{#unless customer.id}}

	{{!-- NOT logged in --}}
    <li><a href="{{urls.auth.login}}">{{lang 'common.login'}}</a></li>
    <li><a href="{{urls.auth.create_account}}">{{lang 'common.sign_up'}}</a></li>



  {{else}}
		{{!-- logged in --}}
